const HeroSection = () => {
  return (
    <section className="bg-[#0D3333] text-[#C1C1C1] py-20 px-6">
      <div className="container mx-auto text-center">
        <h1 className="text-4xl md:text-5xl font-bold mb-6">
          Innovating Agriculture for a Sustainable Future
        </h1>
        <p className="text-xl mb-8 max-w-2xl mx-auto">
          Providing comprehensive agricultural solutions that transform farming practices and increase productivity.
        </p>
        <button className="bg-[#019A32] hover:bg-[#017a28] text-white font-bold py-3 px-8 rounded-lg transition">
          Explore Our Services
        </button>
      </div>
    </section>
  );
};

export default HeroSection;
